def determine_relative_age(rock_layers, intrusions, faults):
    """Determine relative ages using geological principles"""
    # Apply superposition - deeper layers are older
    ages = {layer: index for index, layer in enumerate(reversed(rock_layers))}
    # Cross-cutting relationships - intrusions and faults are younger
    for intrusion in intrusions:
        ages[intrusion] = max(ages.values()) + 1
    for fault in faults:
        ages[fault] = max(ages.values()) + 1
    return ages

def radiometric_age_calculation(parent_isotope, daughter_isotope, half_life):
    """Calculate age using radiometric dating formula"""
    import math
    if parent_isotope <= 0 or daughter_isotope < 0:
        return 0
    total_original = parent_isotope + daughter_isotope
    ratio = parent_isotope / total_original
    if ratio <= 0:
        return float('inf')
    age = (math.log(ratio) / math.log(0.5)) * -half_life
    return age

