def calculate_interval(note1, note2):
    """Calculate interval between two notes in half steps"""
    notes = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"]
    try:
        index1 = notes.index(note1.upper())
        index2 = notes.index(note2.upper())
        return (index2 - index1) % 12
    except ValueError:
        return None

def identify_cadence(chord_progression):
    """Identify common cadence types from chord progression"""
    last_two = chord_progression[-2:] if len(chord_progression) >= 2 else []

    cadences = {
        ("V", "I"): "authentic",
        ("IV", "I"): "plagal",
        ("any", "V"): "half",
        ("V", "vi"): "deceptive"
    }

    for pattern, name in cadences.items():
        if pattern[0] == "any" and last_two[-1] == pattern[1]:
            return name
        elif last_two == list(pattern):
            return name

    return "no common cadence"

def analyze_form(sections):
    """Analyze musical form based on section patterns"""
    if len(sections) == 2 and sections[0] != sections[1]:
        return "binary"
    elif len(sections) == 3 and sections[0] == sections[2]:
        return "ternary"
    elif sections.count(sections[0]) >= 3:
        return "rondo"
    elif len(sections) >= 3 and "development" in sections:
        return "sonata"
    return "free form"

def analyze_melodic_motion(notes):
    """Analyze conjunct vs disjunct motion in melody"""
    intervals = []
    for i in range(1, len(notes)):
        interval = calculate_interval(notes[i-1], notes[i])
        intervals.append(interval)

    steps = sum(1 for i in intervals if i <= 2)
    leaps = len(intervals) - steps

    return {
        "steps": steps,
        "leaps": leaps,
        "motion_type": "conjunct" if steps > leaps else "disjunct"
    }

def roman_numeral_analysis(chord, key):
    """Convert chord to Roman numeral relative to key"""
    scale = major_scale(key)

    # Find which scale degree the chord root matches
    for i, scale_note in enumerate(scale[:-1]):
        if chord[0].upper() == scale_note.upper():
            numerals = ["I", "ii", "iii", "IV", "V", "vi", "vii°"]
            return numerals[i]

    return "non-diatonic"

def identify_texture(voices):
    """Identify musical texture based on voice relationships"""
    if len(voices) == 1:
        return "monophonic"
    elif len(voices) == 2 and all(len(v) > 1 for v in voices):
        return "polyphonic"
    elif any(len(v) == 1 for v in voices):  # One voice has melody
        return "homophonic"
    return "mixed"

