def rhythm_pattern_difficulty(pattern):
    """Assess difficulty level of rhythm patterns"""
    simple_notes = ["quarter", "half", "whole"]
    complex_notes = ["eighth", "sixteenth", "dotted"]

    simple_count = sum(1 for note in pattern if any(s in note for s in simple_notes))
    complex_count = sum(1 for note in pattern if any(c in note for c in complex_notes))

    if complex_count == 0:
        return "beginner"
    elif complex_count <= 2:
        return "intermediate"
    else:
        return "advanced"

def melodic_contour(notes):
    """Analyze the contour (shape) of a melody"""
    if len(notes) < 2:
        return "static"

    directions = []
    for i in range(1, len(notes)):
        if notes[i] > notes[i-1]:
            directions.append("up")
        elif notes[i] < notes[i-1]:
            directions.append("down")
        else:
            directions.append("same")

    if all(d == "up" for d in directions):
        return "ascending"
    elif all(d == "down" for d in directions):
        return "descending"
    elif directions.count("up") > directions.count("down"):
        return "mostly ascending"
    elif directions.count("down") > directions.count("up"):
        return "mostly descending"
    else:
        return "arched"

