class RomanGovernment:
    def __init__(self):
        self.consuls = []
        self.senate_members = []
        self.tribunes = []

    def check_power(self, proposal):
        for tribune in self.tribunes:
            if tribune.veto(proposal):
                return "Vetoed by Tribune"
        for consul in self.consuls:
            if consul.opposes(proposal):
                return "Blocked by Consul"
        return "Approved"

