# Simplifying algebraic expressions
def simplify_expression(terms):
    # Combine like terms
    simplified = {}
    for coefficient, variable in terms:
        simplified[variable] = simplified.get(variable, 0) + coefficient
    return simplified

# Example: 3x + 2y - x + 4y
terms = [(3, 'x'), (2, 'y'), (-1, 'x'), (4, 'y')]
result = simplify_expression(terms)
print(f"Simplified: {result}")  # Output: {'x': 2, 'y': 6}

