class FormulaSolver:
    @staticmethod
    def solve_for_variable(formula, target_var, known_values):
        """Solve formulas for specific variables"""
        if formula == "area_rectangle":
            # A = l × w, solve for missing dimension
            if target_var == "length":
                return known_values['area'] / known_values['width']
            elif target_var == "width":
                return known_values['area'] / known_values['length']

        elif formula == "distance":
            # d = r × t
            if target_var == "rate":
                return known_values['distance'] / known_values['time']
            elif target_var == "time":
                return known_values['distance'] / known_values['rate']

