# Function concepts and operations
class FunctionOperations:
    def __init__(self, expression):
        self.expression = expression

    def evaluate(self, x):
        """Evaluate function at specific x value"""
        return eval(self.expression.replace('x', str(x)))

    def generate_table(self, x_values):
        """Generate input-output table"""
        table = {}
        for x in x_values:
            table[x] = self.evaluate(x)
        return table

