# Solving systems by graphing
def find_intersection(m1, b1, m2, b2, x_range=(-10, 10), tolerance=0.01):
    """Find intersection point of two lines"""
    for x in [i * 0.1 for i in range(x_range[0]*10, x_range[1]*10)]:
        y1 = m1 * x + b1
        y2 = m2 * x + b2
        if abs(y1 - y2) < tolerance:
            return (round(x, 1), round(y1, 1))
    return None  # No intersection found

# Example: y = 2x + 1 and y = -x + 4
