# Polynomial multiplication
class PolynomialMultiplier:
    @staticmethod
    def multiply(poly1, poly2):
        """Multiply two polynomials"""
        result_degree = (len(poly1.coefficients) - 1) + (len(poly2.coefficients) - 1)
        result = [0] * (result_degree + 1)

        for i, coef1 in enumerate(poly1.coefficients):
            power1 = len(poly1.coefficients) - 1 - i
            for j, coef2 in enumerate(poly2.coefficients):
                power2 = len(poly2.coefficients) - 1 - j
                result_power = power1 + power2
                result[result_degree - result_power] += coef1 * coef2

        return Polynomial(result)

    @staticmethod
    def foil(a, b, c, d):
        """FOIL method for (ax + b)(cx + d)"""
        first = a * c
        outer = a * d
        inner = b * c
        last = b * d
        return f"{first}x² + {outer + inner}x + {last}"

# Example: (2x + 3)(x - 1)
print(f"FOIL result: {PolynomialMultiplier.foil(2, 3, 1, -1)}")

