# Solving quadratic equations
class QuadraticSolver:
    @staticmethod
    def solve_quadratic(a, b, c):
        """Solve ax² + bx + c = 0"""
        discriminant = b**2 - 4*a*c

        if discriminant > 0:
            root1 = (-b + math.sqrt(discriminant)) / (2*a)
            root2 = (-b - math.sqrt(discriminant)) / (2*a)
            return f"Two real solutions: {root1:.2f}, {root2:.2f}"
        elif discriminant == 0:
            root = -b / (2*a)
            return f"One real solution: {root:.2f}"
        else:
            real_part = -b / (2*a)
            imaginary_part = math.sqrt(-discriminant) / (2*a)
            return f"Two complex solutions: {real_part:.2f} ± {imaginary_part:.2f}i"

    @staticmethod
    def analyze_discriminant(a, b, c):
        discriminant = b**2 - 4*a*c
        if discriminant > 0:
            return "Two x-intercepts"
        elif discriminant == 0:
            return "One x-intercept (vertex on x-axis)"
        else:
            return "No x-intercepts"

# Example: Solve x² - 5x + 6 = 0
