class Triangle:
    def __init__(self, side1, side2, side3, angle1, angle2, angle3):
        self.sides = [side1, side2, side3]
        self.angles = [angle1, angle2, angle3]

    def verify_triangle(self):
        # Check if sides can form a triangle
        a, b, c = sorted(self.sides)
        return a + b > c and sum(self.angles) == 180

    def perimeter(self):
        return sum(self.sides)

