class PythagoreanTheorem:
    @staticmethod
    def find_hypotenuse(a, b):
        """Find hypotenuse given two legs"""
        return (a**2 + b**2)**0.5

    @staticmethod
    def find_leg(hypotenuse, other_leg):
        """Find missing leg given hypotenuse and one leg"""
        return (hypotenuse**2 - other_leg**2)**0.5

    @staticmethod
    def is_right_triangle(a, b, c):
        """Check if triangle with sides a,b,c is right"""
        sides = sorted([a, b, c])
        return abs(sides[0]**2 + sides[1]**2 - sides[2]**2) < 0.0001

