class PythagoreanTriples:
    @staticmethod
    def generate_primitive_triples(limit):
        """Generate primitive Pythagorean triples up to given limit"""
        triples = []
        for m in range(2, limit):
            for n in range(1, m):
                if (m - n) % 2 == 1 and math.gcd(m, n) == 1:  # One odd, one even, coprime
                    a = m**2 - n**2
                    b = 2 * m * n
                    c = m**2 + n**2
                    if c <= limit:
                        triples.append(sorted([a, b, c]))
        return triples

    @staticmethod
    def is_pythagorean_triple(a, b, c):
        """Check if three numbers form a Pythagorean triple"""
        sides = sorted([a, b, c])
        return sides[0]**2 + sides[1]**2 == sides[2]**2

