    def from_short_leg(short_leg):
        """Find all sides given short leg"""
        return {
            'short_leg': short_leg,
            'long_leg': short_leg * math.sqrt(3),
            'hypotenuse': short_leg * 2
        }

    @staticmethod
    def from_long_leg(long_leg):
        """Find all sides given long leg"""
        short_leg = long_leg / math.sqrt(3)
        return {
            'short_leg': short_leg,
            'long_leg': long_leg,
            'hypotenuse': short_leg * 2
        }

    @staticmethod
    def area(short_leg, long_leg):
        """Calculate area"""
        return 0.5 * short_leg * long_leg

