class TriangleCongruence:
    @staticmethod
    def check_SSS(sides1, sides2):
        """Check Side-Side-Side congruence"""
        return sorted(sides1) == sorted(sides2)

    @staticmethod
    def check_SAS(sides1, angle1, sides2, angle2):
        """Check Side-Angle-Side congruence"""
        # sides1 and sides2 are tuples of two sides
        # angle1 and angle2 are the included angles
        return (sorted(sides1) == sorted(sides2) and 
                abs(angle1 - angle2) < 0.001)

    @staticmethod
    def check_ASA(angles1, side1, angles2, side2):
        """Check Angle-Side-Angle congruence"""
        # angles1 and angles2 are tuples of two angles
        # side1 and side2 are the included sides
        return (sorted(angles1) == sorted(angles2) and 
                abs(side1 - side2) < 0.001)

