class IntermediateValueTheorem:
    @staticmethod
    def apply_ivt(f, a, b, k):
        """Apply Intermediate Value Theorem to find where f(x) = k"""
        if not (min(f(a), f(b)) <= k <= max(f(a), f(b))):
            return f"IVT doesn't apply: {k} not between f({a}) and f({b})"

        # Simple bisection method to find approximate root
        left, right = a, b
        for _ in range(20):  # 20 iterations for precision
            mid = (left + right) / 2
            if abs(f(mid) - k) < 0.0001:
                return f"Found approximate solution: x ≈ {mid:.4f}"
            elif (f(mid) - k) * (f(left) - k) < 0:
                right = mid
            else:
                left = mid

        return f"Approximate solution: x ≈ {(left + right)/2:.4f}"

