class DifferentiationRules:
    @staticmethod
    def power_rule(n):
        """Return derivative function for x^n"""
        return lambda x: n * x**(n-1)

    @staticmethod
    def product_rule(f, f_prime, g, g_prime):
        """Apply product rule"""
        return lambda x: f_prime(x) * g(x) + f(x) * g_prime(x)

    @staticmethod
    def quotient_rule(f, f_prime, g, g_prime):
        """Apply quotient rule"""
        return lambda x: (f_prime(x) * g(x) - f(x) * g_prime(x)) / (g(x)**2)

# Example using power rule
