class ChainRule:
    @staticmethod
    def apply_chain_rule(outer_func, outer_deriv, inner_func, inner_deriv):
        """Apply chain rule to composite function"""
        return lambda x: outer_deriv(inner_func(x)) * inner_deriv(x)

    @staticmethod
    def multiple_composition(functions, derivatives):
        """Handle chain rule for multiple compositions"""
        def composite_derivative(x):
            result = 1
            current_x = x
            for i in range(len(functions)):
                result *= derivatives[i](current_x)
                if i < len(functions) - 1:
                    current_x = functions[i](current_x)
            return result
        return composite_derivative

