class CurveAnalysis:
    @staticmethod
    def analyze_function(f, f_prime, f_double_prime):
        """Analyze function for graphing"""
        import numpy as np

        # Find critical points
        critical_points = []
        x_values = np.linspace(-5, 5, 1000)

        # Simple numerical approach to find where derivative ~0
        for i in range(1, len(x_values)-1):
            if f_prime(x_values[i-1]) * f_prime(x_values[i+1]) < 0:
                critical_points.append(x_values[i])

        # Analyze concavity
        concave_up = []
        concave_down = []

        for x in x_values:
            if f_double_prime(x) > 0:
                concave_up.append(x)
            elif f_double_prime(x) < 0:
                concave_down.append(x)

        return {
            'critical_points': critical_points,
            'concave_up_intervals': concave_up,
            'concave_down_intervals': concave_down
        }

# Example: f(x) = x^3 - 3x
