class SequencesSeries:
    @staticmethod
    def geometric_series(a, r, n_terms=10):
        """Compute geometric series sum"""
        if abs(r) < 1:
            exact_sum = a / (1 - r)
        else:
            exact_sum = "diverges"

        partial_sum = sum(a * r**i for i in range(n_terms))

        return f"Geometric series: a={a}, r={r}"

    @staticmethod
    def taylor_series():
        """Common Taylor series expansions"""
        series = {
            "e^x": "1 + x + x²/2! + x³/3! + ...",
            "sin x": "x - x³/3! + x⁵/5! - x⁷/7! + ...",
            "cos x": "1 - x²/2! + x⁴/4! - x⁶/6! + ...",
            "1/(1-x)": "1 + x + x² + x³ + ... (for |x|<1)"
        }

        print("Taylor Series Expansions:")
        for func, expansion in series.items():
            print(f"{func}: {expansion}")

        return series

