class PulleySystem:
    @staticmethod
    def atwoods_machine(m1, m2):
        """Analyze Atwood's machine: two masses connected over pulley"""
        g = 9.8

        if m1 > m2:
            net_force = (m1 - m2) * g
            total_mass = m1 + m2
            acceleration = net_force / total_mass
            tension = m2 * g + m2 * acceleration

            print(f"Atwood's Machine (m1={m1}kg, m2={m2}kg):")
            print(f"Acceleration: {acceleration:.2f} m/s²")
            print(f"Tension: {tension:.2f} N")

            return acceleration, tension
        else:
            return "m1 must be greater than m2 for this simple case"

    @staticmethod
    def mechanical_advantage(configuration):
        """Calculate mechanical advantage of pulley system"""
        if configuration == "single_fixed":
            return 1  # Only changes direction
        elif configuration == "single_movable":
            return 2  # Halves required force
        elif configuration == "double":
            return 4  # Quarters required force

# Example: Atwood's machine
accel, tension = PulleySystem.atwoods_machine(6, 4)

advantage = PulleySystem.mechanical_advantage("single_movable")
print(f"Mechanical advantage: {advantage}")

