class Momentum:
    @staticmethod
    def calculate_momentum(mass, velocity):
        """Calculate linear momentum"""
        return mass * velocity

    @staticmethod
    def momentum_change(initial_momentum, final_momentum):
        """Calculate change in momentum"""
        return final_momentum - initial_momentum

