// Island species diversity estimation (simplified MacArthur-Wilson)
function estimateSpeciesDiversity(islandArea, distanceFromMainland) {
    const areaEffect = Math.log(islandArea);
    const distanceEffect = 1 / Math.sqrt(distanceFromMainland);
    return Math.round(areaEffect * distanceEffect * 10); // Scaling factor
}

// Population growth models
function exponentialGrowth(initialPop, growthRate, time) {
    return initialPop * Math.exp(growthRate * time);
}
function logisticGrowth(initialPop, growthRate, carryingCapacity, time) {
    return carryingCapacity / (1 + ((carryingCapacity - initialPop) / initialPop) * Math.exp(-growthRate * time));
}

// Biodiversity index calculation (simplified Shannon-Wiener)
function shannonWienerIndex(speciesCounts) {
    const total = speciesCounts.reduce((sum, count) => sum + count, 0);
    let index = 0;
    for (const count of speciesCounts) {
        const proportion = count / total;
        if (proportion > 0) {
            index -= proportion * Math.log(proportion);
        }
    }
    return index;
}

// Threat assessment scoring
function assessEcosystemThreat(habitatLoss, pollution, invasives, climateChange) {
    const score = habitatLoss * 0.4 + pollution * 0.2 + invasives * 0.2 + climateChange * 0.2;
    if (score > 0.7) return 'Critical threat';
    if (score > 0.4) return 'High threat';
    return 'Moderate threat';
}

// Conservation priority calculation
function conservationPriority(species, threatLevel, ecologicalRole) {
    let priority = threatLevel;
    if (ecologicalRole === 'keystone') priority *= 1.5;
    if (ecologicalRole === 'indicator') priority *= 1.2;
    return priority;
}

