// Matrix class implementation
class Matrix {
    constructor(rows, cols, data = null) {
        this.rows = rows;
        this.cols = cols;
        this.data = data || Array(rows).fill().map(() => Array(cols).fill(0));
    }
    get(i, j) {
        return this.data[i][j];
    }
    set(i, j, value) {
        this.data[i][j] = value;
    }
}

// Matrix multiplication
function multiplyMatrices(A, B) {
    if (A.cols !== B.rows) throw new Error("Incompatible dimensions");
    const result = new Matrix(A.rows, B.cols);
    for (let i = 0; i < A.rows; i++) {
        for (let j = 0; j < B.cols; j++) {
            let sum = 0;
            for (let k = 0; k < A.cols; k++) {
                sum += A.get(i, k) * B.get(k, j);
            }
            result.set(i, j, sum);
        }
    }
    return result;
}

// 2x2 determinant calculation
function determinant2x2(matrix) {
    return matrix.get(0,0) * matrix.get(1,1) - matrix.get(0,1) * matrix.get(1,0);
}
// 3x3 determinant using rule of Sarrus
function determinant3x3(matrix) {
    const a = matrix.get(0,0), b = matrix.get(0,1), c = matrix.get(0,2);
    const d = matrix.get(1,0), e = matrix.get(1,1), f = matrix.get(1,2);
    const g = matrix.get(2,0), h = matrix.get(2,1), i = matrix.get(2,2);
    return a*e*i + b*f*g + c*d*h - c*e*g - b*d*i - a*f*h;
}

// 2x2 matrix inverse
function inverse2x2(matrix) {
    const det = determinant2x2(matrix);
    if (Math.abs(det) < 1e-10) throw new Error("Matrix is singular");
    const result = new Matrix(2, 2);
    result.set(0, 0, matrix.get(1,1) / det);
    result.set(0, 1, -matrix.get(0,1) / det);
    result.set(1, 0, -matrix.get(1,0) / det);
    result.set(1, 1, matrix.get(0,0) / det);
    return result;
}

// Representing system as augmented matrix
function createAugmentedMatrix(A, b) {
    const augmented = new Matrix(A.rows, A.cols + 1);
    for (let i = 0; i < A.rows; i++) {
        for (let j = 0; j < A.cols; j++) {
            augmented.set(i, j, A.get(i, j));
        }
        augmented.set(i, A.cols, b[i]);
    }
    return augmented;
}

// Gaussian elimination implementation
function gaussianElimination(augmented) {
    const n = augmented.rows;
    // Forward elimination
    for (let i = 0; i < n; i++) {
        // Find pivot row
        let maxRow = i;
        for (let k = i + 1; k < n; k++) {
            if (Math.abs(augmented.get(k, i)) > Math.abs(augmented.get(maxRow, i))) {
                maxRow = k;
            }
        }
        // Swap rows if needed
        if (maxRow !== i) {
            for (let j = 0; j <= n; j++) {
                const temp = augmented.get(i, j);
                augmented.set(i, j, augmented.get(maxRow, j));
                augmented.set(maxRow, j, temp);
            }
        }
        // Eliminate below
        for (let k = i + 1; k < n; k++) {
            const factor = augmented.get(k, i) / augmented.get(i, i);
            for (let j = i; j <= n; j++) {
                augmented.set(k, j, augmented.get(k, j) - factor * augmented.get(i, j));
            }
        }
    }
    return augmented;
}

