// Eigenvalues for 2x2
function eigenvalues2x2(matrix) {
    const trace = matrix.get(0,0) + matrix.get(1,1);
    const det = determinant2x2(matrix);
    const discriminant = trace * trace - 4 * det;
    if (discriminant < 0) return ['Complex eigenvalues'];
    const sqrtDisc = Math.sqrt(discriminant);
    return [(trace + sqrtDisc) / 2, (trace - sqrtDisc) / 2];
}

