// Rotation transformation matrix
function rotationMatrix(angle) {
    const cos = Math.cos(angle);
    const sin = Math.sin(angle);
    const R = new Matrix(2, 2);
    R.set(0, 0, cos);
    R.set(0, 1, -sin);
    R.set(1, 0, sin);
    R.set(1, 1, cos);
    return R;
}
// Apply rotation to vector
function rotateVector(v, angle) {
    const R = rotationMatrix(angle);
    const x = R.get(0,0) * v.x + R.get(0,1) * v.y;
    const y = R.get(1,0) * v.x + R.get(1,1) * v.y;
    return new Vector(x, y);
}

// Various vector norms
function normL1(v) {
    return Math.abs(v.x) + Math.abs(v.y) + Math.abs(v.z);
}
function normL2(v) {
    return Math.sqrt(v.x*v.x + v.y*v.y + v.z*v.z);
}
function normLinf(v) {
    return Math.max(Math.abs(v.x), Math.abs(v.y), Math.abs(v.z));
}

// Vector projection
function project(u, v) {
    // Project vector u onto v
    const scalar = (u.x*v.x + u.y*v.y + u.z*v.z) / (v.x*v.x + v.y*v.y + v.z*v.z);
    return new Vector(scalar*v.x, scalar*v.y, scalar*v.z);
}
// Dot product
function dot(u, v) {
    return u.x*v.x + u.y*v.y + u.z*v.z;
}

// Simple linear regression using normal equations
function linearRegression(X, y) {
    // X is feature matrix, y is target vector
    // β = (XᵀX)⁻¹Xᵀy
    const Xt = transpose(X);
    const XtX = multiplyMatrices(Xt, X);
    const XtX_inv = inverse2x2(XtX); // For 2D case
    const Xty = multiplyMatrices(Xt, new Matrix(y.length, 1, [y]));
    return multiplyMatrices(XtX_inv, Xty);
}

