// Timing and spacing calculation
function calculateSpacing(totalFrames, easeType) {
    const spacing = [];
    for (let i = 0; i < totalFrames; i++) {
        if (easeType === 'easeIn') {
            spacing.push(i * i / (totalFrames * totalFrames));
        } else if (easeType === 'easeOut') {
            spacing.push(1 - (1 - i / totalFrames) ** 2);
        }
    }
    return spacing;
}

