// Onion skinning visualization concept
function showOnionSkin(frames, currentFrame, layers = 3) {
    const visibleFrames = [];
    for (let i = -layers; i <= layers; i++) {
        const frameIndex = currentFrame + i;
        if (frameIndex >= 0 && frameIndex < frames.length) {
            visibleFrames.push({
                frame: frames[frameIndex],
                opacity: 1 - Math.abs(i) / (layers + 1)
            });
        }
    }
    return visibleFrames;
}

