// De Morgan's Laws implementation
function deMorgan1(p, q) {
    // ¬(p ∧ q) ≡ ¬p ∨ ¬q
    return logicalNot(logicalAnd(p, q)) === logicalOr(logicalNot(p), logicalNot(q));
}
function deMorgan2(p, q) {
    // ¬(p ∨ q) ≡ ¬p ∧ ¬q
    return logicalNot(logicalOr(p, q)) === logicalAnd(logicalNot(p), logicalNot(q));
}

// Conditional and related statements
function conditional(p, q) {
    return !p || q;  // p → q ≡ ¬p ∨ q
}
function converse(p, q) {
    return conditional(q, p);
}
function contrapositive(p, q) {
    return conditional(!q, !p);
}
function inverse(p, q) {
    return conditional(!p, !q);
}

// Set representation and operations
class Set {
    constructor(elements = []) {
        this.elements = [...new Set(elements)]; // Remove duplicates
    }
    has(element) {
        return this.elements.includes(element);
    }
    add(element) {
        if (!this.has(element)) {
            this.elements.push(element);
        }
    }
    toString() {
        return `{${this.elements.join(', ')}}`;
    }
}

// Set operations implementation
Set.prototype.union = function(other) {
    return new Set([...this.elements, ...other.elements]);
};
Set.prototype.intersection = function(other) {
    return new Set(this.elements.filter(x => other.has(x)));
};
Set.prototype.difference = function(other) {
    return new Set(this.elements.filter(x => !other.has(x)));
};
// Example usage
const A = new Set([1, 2, 3]);
const B = new Set([2, 3, 4]);
