// Power set generation
function powerSet(set) {
    const elements = set.elements;
    const result = [new Set()]; // Start with empty set

    for (const element of elements) {
        const newSubsets = [];
        for (const subset of result) {
            const newSubset = new Set([...subset.elements, element]);
            newSubsets.push(newSubset);
        }
        result.push(...newSubsets);
    }
    return result;
}
const exampleSet = new Set([1, 2]);
console.log(powerSet(exampleSet).map(s => s.toString()));

