// Binary relation implementation
class Relation {
    constructor(pairs = []) {
        this.pairs = pairs;
    }
    domain() {
        return new Set(this.pairs.map(pair => pair[0]));
    }
    range() {
        return new Set(this.pairs.map(pair => pair[1]));
    }
    isReflexive(universe) {
        return universe.elements.every(x => 
            this.pairs.some(pair => pair[0] === x && pair[1] === x)
        );
    }
}

// Equivalence relation properties
function isEquivalenceRelation(relation, universe) {
    return (
        relation.isReflexive(universe) &&
        relation.isSymmetric() &&
        relation.isTransitive()
    );
}
Relation.prototype.isSymmetric = function() {
    return this.pairs.every(pair => 
        this.pairs.some(reversePair => 
            reversePair[0] === pair[1] && reversePair[1] === pair[0]
        )
    );
};

// Function as a special relation
class Function extends Relation {
    isFunction() {
        const inputs = this.pairs.map(pair => pair[0]);
        return inputs.length === new Set(inputs).size;
    }
    isInjective() {
        const outputs = this.pairs.map(pair => pair[1]);
        return outputs.length === new Set(outputs).size;
    }
    isSurjective(codomain) {
        const rangeSet = this.range();
        return codomain.elements.every(y => rangeSet.has(y));
    }
}

// Function composition
function compose(f, g) {
    return function(x) {
        return f(g(x));
    };
}
// Inverse function (if bijective)
Function.prototype.inverse = function() {
    if (this.isInjective() && this.isSurjective(this.codomain)) {
        const inversePairs = this.pairs.map(pair => [pair[1], pair[0]]);
        return new Function(inversePairs);
    }
    return null; // No inverse exists
};

