// Discrete random variable implementation
class DiscreteRandomVariable {
    constructor(values, probabilities) {
        this.values = values;
        this.probabilities = probabilities;
    }
    expectedValue() {
        return this.values.reduce((sum, value, i) => 
            sum + value * this.probabilities[i], 0
        );
    }
    variance() {
        const mean = this.expectedValue();
        return this.values.reduce((sum, value, i) => 
            sum + this.probabilities[i] * (value - mean) ** 2, 0
        );
    }
}

// Normal distribution PDF
function normalPDF(x, mean = 0, stdDev = 1) {
    const exponent = -((x - mean) ** 2) / (2 * stdDev ** 2);
    return (1 / (stdDev * Math.sqrt(2 * Math.PI))) * Math.exp(exponent);
}
// Probability in interval using approximation
function probabilityInInterval(lower, upper, mean, stdDev, steps = 1000) {
    let area = 0;
    const dx = (upper - lower) / steps;
    for (let i = 0; i < steps; i++) {
        const x = lower + i * dx;
        area += normalPDF(x, mean, stdDev) * dx;
    }
    return area;
}

// Expected value and variance calculations
function expectedValue(values, probabilities) {
    return values.reduce((sum, val, i) => sum + val * probabilities[i], 0);
}
function variance(values, probabilities) {
    const mean = expectedValue(values, probabilities);
    return values.reduce((sum, val, i) => 
        sum + probabilities[i] * (val - mean) ** 2, 0
    );
}
function standardDeviation(values, probabilities) {
    return Math.sqrt(variance(values, probabilities));
}

// Binomial distribution probability
function binomialProbability(n, k, p) {
    // P(X = k) = C(n,k) * p^k * (1-p)^(n-k)
    const combination = factorial(n) / (factorial(k) * factorial(n - k));
    return combination * (p ** k) * ((1 - p) ** (n - k));
}
// Poisson distribution probability
function poissonProbability(lambda, k) {
    // P(X = k) = (λ^k * e^{-λ}) / k!
    return (Math.pow(lambda, k) * Math.exp(-lambda)) / factorial(k);
}
function factorial(n) {
    return n <= 1 ? 1 : n * factorial(n - 1);
}

// Simulating sampling distribution of the mean
function simulateSamplingDistribution(population, sampleSize, numSamples) {
    const sampleMeans = [];
    for (let i = 0; i < numSamples; i++) {
        const sample = [];
        for (let j = 0; j < sampleSize; j++) {
            const randomIndex = Math.floor(Math.random() * population.length);
            sample.push(population[randomIndex]);
        }
        sampleMeans.push(mean(sample));
    }
    return sampleMeans;
}
function mean(array) {
    return array.reduce((sum, val) => sum + val, 0) / array.length;
}

// Confidence interval for population mean
function confidenceInterval(sample, confidenceLevel = 0.95) {
    const n = sample.length;
    const sampleMean = mean(sample);
    const sampleStd = standardDeviation(sample);
    const zValue = getZValue(confidenceLevel); // From normal distribution
    const marginOfError = zValue * (sampleStd / Math.sqrt(n));
    return {
        lower: sampleMean - marginOfError,
        upper: sampleMean + marginOfError,
        marginOfError: marginOfError
    };
}
function standardDeviation(array) {
    const avg = mean(array);
    const squareDiffs = array.map(value => (value - avg) ** 2);
    return Math.sqrt(mean(squareDiffs));
}

// One-sample t-test implementation
function tTest(sample, hypothesizedMean) {
    const n = sample.length;
    const sampleMean = mean(sample);
    const sampleStd = standardDeviation(sample);
    const standardError = sampleStd / Math.sqrt(n);
    const tStatistic = (sampleMean - hypothesizedMean) / standardError;
    // Degrees of freedom = n - 1
    const df = n - 1;
    // p-value would be calculated using t-distribution
    return {tStatistic, df, standardError};
}

