// Pearson correlation coefficient
function correlation(x, y) {
    const n = x.length;
    const meanX = mean(x);
    const meanY = mean(y);

    const numerator = x.reduce((sum, xi, i) => 
        sum + (xi - meanX) * (y[i] - meanY), 0
    );
    const denomX = Math.sqrt(x.reduce((sum, xi) => sum + (xi - meanX) ** 2, 0));
    const denomY = Math.sqrt(y.reduce((sum, yi) => sum + (yi - meanY) ** 2, 0));

    return numerator / (denomX * denomY);
}
// Interpretation guide
function interpretCorrelation(r) {
    const absR = Math.abs(r);
    if (absR < 0.3) return 'Weak correlation';
    if (absR < 0.7) return 'Moderate correlation';
    return 'Strong correlation';
}

// Simple linear regression
function linearRegression(x, y) {
    const n = x.length;
    const meanX = mean(x);
    const meanY = mean(y);

    const numerator = x.reduce((sum, xi, i) => 
        sum + (xi - meanX) * (y[i] - meanY), 0
    );
    const denominator = x.reduce((sum, xi) => sum + (xi - meanX) ** 2, 0);

    const slope = numerator / denominator;
    const intercept = meanY - slope * meanX;

    return {slope, intercept};
}
// Prediction using regression model
function predict(regression, x) {
    return regression.intercept + regression.slope * x;
}

