// Timing and spacing calculation
function calculateSpacing(totalFrames, easeType) {
    const spacing = [];
    for (let i = 0; i < totalFrames; i++) {
        if (easeType === 'easeIn') {
            spacing.push(i * i / (totalFrames * totalFrames));
        } else if (easeType === 'easeOut') {
            spacing.push(1 - (1 - i / totalFrames) ** 2);
        }
    }
    return spacing;
}

// Follow through simulation for character with cape
class CharacterWithCape {
    constructor() {
        this.bodyPosition = 0;
        this.capePosition = 0;
    }
    moveBody(newPosition) {
        this.bodyPosition = newPosition;
        // Cape follows with delay
        this.capePosition = this.capePosition + (this.bodyPosition - this.capePosition) * 0.7;
    }
}

// Easing functions for animation
const easingFunctions = {
    easeIn: (t) => t * t,
    easeOut: (t) => 1 - (1 - t) * (1 - t),
    easeInOut: (t) => t < 0.5 ? 2 * t * t : 1 - Math.pow(-2 * t + 2, 2) / 2
};
function applyEasing(start, end, progress, easeType) {
    const eased = easingFunctions[easeType](progress);
    return start + (end - start) * eased;
}

