// Easing functions for animation
const easingFunctions = {
    easeIn: (t) => t * t,
    easeOut: (t) => 1 - (1 - t) * (1 - t),
    easeInOut: (t) => t < 0.5 ? 2 * t * t : 1 - Math.pow(-2 * t + 2, 2) / 2
};
function applyEasing(start, end, progress, easeType) {
    const eased = easingFunctions[easeType](progress);
    return start + (end - start) * eased;
}

