def depth_of_field(aperture, focal_length, distance, circle_of_confusion=0.03):
    """Calculate approximate depth of field"""
    hyperfocal = (focal_length**2) / (aperture * circle_of_confusion)
    near_limit = (hyperfocal * distance) / (hyperfocal + distance)
    far_limit = (hyperfocal * distance) / (hyperfocal - distance) if distance < hyperfocal else float('inf')
    return (near_limit, far_limit)

def calculate_interval(note1, note2):
    """Calculate interval between two notes in half steps"""
    notes = ["C", "C#", "D", "D#", "E", "F", "F#", "G", "G#", "A", "A#", "B"]
    try:
        index1 = notes.index(note1.upper())
        index2 = notes.index(note2.upper())
        return (index2 - index1) % 12
    except ValueError:
        return None

def identify_cadence(chord_progression):
    """Identify common cadence types from chord progression"""
    last_two = chord_progression[-2:] if len(chord_progression) >= 2 else []

    cadences = {
        ("V", "I"): "authentic",
        ("IV", "I"): "plagal",
        ("any", "V"): "half",
        ("V", "vi"): "deceptive"
    }

    for pattern, name in cadences.items():
        if pattern[0] == "any" and last_two[-1] == pattern[1]:
            return name
        elif last_two == list(pattern):
            return name

    return "no common cadence"

def analyze_form(sections):
    """Analyze musical form based on section patterns"""
    if len(sections) == 2 and sections[0] != sections[1]:
        return "binary"
    elif len(sections) == 3 and sections[0] == sections[2]:
        return "ternary"
    elif sections.count(sections[0]) >= 3:
        return "rondo"
    elif len(sections) >= 3 and "development" in sections:
        return "sonata"
    return "free form"

def analyze_melodic_motion(notes):
    """Analyze conjunct vs disjunct motion in melody"""
    intervals = []
    for i in range(1, len(notes)):
        interval = calculate_interval(notes[i-1], notes[i])
        intervals.append(interval)

    steps = sum(1 for i in intervals if i <= 2)
    leaps = len(intervals) - steps

    return {
        "steps": steps,
        "leaps": leaps,
        "motion_type": "conjunct" if steps > leaps else "disjunct"
    }

def roman_numeral_analysis(chord, key):
    """Convert chord to Roman numeral relative to key"""
    scale = major_scale(key)

    # Find which scale degree the chord root matches
    for i, scale_note in enumerate(scale[:-1]):
        if chord[0].upper() == scale_note.upper():
            numerals = ["I", "ii", "iii", "IV", "V", "vi", "vii°"]
            return numerals[i]

    return "non-diatonic"

def identify_texture(voices):
    """Identify musical texture based on voice relationships"""
    if len(voices) == 1:
        return "monophonic"
    elif len(voices) == 2 and all(len(v) > 1 for v in voices):
        return "polyphonic"
    elif any(len(v) == 1 for v in voices):  # One voice has melody
        return "homophonic"
    return "mixed"

def chord_quality_descriptors():
    """Provide emotional descriptors for chord qualities"""
    return {
        "major": ["happy", "bright", "stable", "triumphant"],
        "minor": ["sad", "mysterious", "melancholic", "thoughtful"],
        "diminished": ["tense", "unstable", "suspenseful", "dark"],
        "augmented": ["mysterious", "dreamy", "floating", "unresolved"],
        "dominant seventh": ["bluesy", "tense", "yearning", "jazzy"],
        "major seventh": ["dreamy", "wistful", "sophisticated", "jazzy"]
    }

def rhythm_pattern_difficulty(pattern):
    """Assess difficulty level of rhythm patterns"""
    simple_notes = ["quarter", "half", "whole"]
    complex_notes = ["eighth", "sixteenth", "dotted"]

    simple_count = sum(1 for note in pattern if any(s in note for s in simple_notes))
    complex_count = sum(1 for note in pattern if any(c in note for c in complex_notes))

    if complex_count == 0:
        return "beginner"
    elif complex_count <= 2:
        return "intermediate"
    else:
        return "advanced"

def melodic_contour(notes):
    """Analyze the contour (shape) of a melody"""
    if len(notes) < 2:
        return "static"

    directions = []
    for i in range(1, len(notes)):
        if notes[i] > notes[i-1]:
            directions.append("up")
        elif notes[i] < notes[i-1]:
            directions.append("down")
        else:
            directions.append("same")

    if all(d == "up" for d in directions):
        return "ascending"
    elif all(d == "down" for d in directions):
        return "descending"
    elif directions.count("up") > directions.count("down"):
        return "mostly ascending"
    elif directions.count("down") > directions.count("up"):
        return "mostly descending"
    else:
        return "arched"

