def improvisation_approaches(chord_progression, key):
    """Suggest improvisation approaches for chord progression"""
    approaches = []

    if len(chord_progression) <= 4:
        approaches.append("Use pentatonic scale of the key")

    if any("7" in chord for chord in chord_progression):
        approaches.append("Outline chord arpeggios")

    if "ii" in chord_progression and "V" in chord_progression:
        approaches.append("Use bebop scales over ii-V segments")

    approaches.append("Create and develop short motives")
    approaches.append("Balance stepwise motion with leaps")

    return approaches

