def analyze_writing_style(text_sample):
    """Basic analysis of writing style elements"""
    analysis = {}
    words = text_sample.split()
    sentences = text_sample.count('.') + text_sample.count('!') + text_sample.count('?')

    analysis["average_sentence_length"] = len(words) / sentences if sentences > 0 else 0
    analysis["descriptive_ratio"] = sum(1 for word in words if len(word) > 6) / len(words)

    if analysis["average_sentence_length"] < 10:
        analysis["style"] = "concise"
    elif analysis["average_sentence_length"] > 20:
        analysis["style"] = "elaborate"
    else:
        analysis["style"] = "balanced"

    return analysis

